import React, { Component } from 'react';

export default class Cooking extends Component  {
    constructor (props) {
        super(props);
        this.state = {
            location:0,
            hitflg:true,
            hitpoint:30,
            value:0,
            point:0
        };
    }

    test = (e) =>{
        console.log("test:"+e.target.value);
        console.log(e.target);
        this.setState({
            value:e.target.value + 4
        })
    }

    render() {
        const {location} = this.state;
        const {hitpoint} = this.state;
        const {hitflg} = this.state;
        const {point} = this.state;

        if(hitflg === true){
            console.log("location:"+location);
        }

        return (<div>
            <div>
                <meta http-equiv="CONTENT-TYPE" content="text/html; charset=utf-8" />
                <script src="https://code.jquery.com/jquery-3.3.1.min.js"></script>
                <script src="https://cdnjs.cloudflare.com/ajax/libs/Chart.js/2.7.3/Chart.min.js"></script>
                <h1>ポケモンチャート</h1>
            </div>
            <div>
    <p>ポケモン名</p>
    <input type="text" id="pokeName" value="" maxlength="20"/>
    <input type="button" value="Click" onClick="getCSV(1)"/>
    <p>努力値</p>
    <label>H</label>
    <input type="number" name="num1" min="0" max="252" value={this.state.value} step="4" id="HPDoryoku" onClick={this.test}/>
    <label>A</label>
    <input type="number" name="num1" min="0" max="252" value="0" step="4" id="AtkDoryoku" OnClick="test()"/>
    <label>D</label>
    <input type="number" name="num1" min="0" max="252" value="0" step="4" id="DefDoryoku" OnClick="test()"/>
    <label>C</label>
    <input type="number" name="num1" min="0" max="252" value="0" step="4" id="CDoryoku" OnClick="test()"/>
    <label>B</label>
    <input type="number" name="num1" min="0" max="252" value="0" step="4" id="BDoryoku" OnClick="test()"/>
    <label>S</label>
    <input type="number" name="num1" min="0" max="252" value="0" step="4" id="SPDoryoku" OnClick="test()"/>

    <p>個体値</p>
    <label>H</label>
    <input type="number" name="num1" min="0" max="31" value="0" step="1" id="HPKotai" OnClick="test()"/>
    <label>A</label>
    <input type="number" name="num1" min="0" max="31" value="0" step="1" id="AtkKotai" OnClick="test()"/>
    <label>D</label>
    <input type="number" name="num1" min="0" max="31" value="0" step="1" id="DefKotai" OnClick="test()"/>
    <label>C</label>
    <input type="number" name="num1" min="0" max="31" value="0" step="1" id="CKotai" OnClick="test()"/>
    <label>B</label>
    <input type="number" name="num1" min="0" max="31" value="0" step="1" id="BKotai" OnClick="test()"/>
    <label>S</label>
    <input type="number" name="num1" min="0" max="31" value="0" step="1" id="SPKotai" OnClick="test()"/>
  </div>
        </div>);
    }
  }
